"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[1995],{21995:n=>{n.exports=JSON.parse('{"java":{"language":"java","code":"class Solution {\\n    public int longestSubstring(String s, int k) {\\n        int[] chars = new int[26];\\n        for(int i=0; i<s.length(); i++) {\\n            chars[s.charAt(i) - \'a\'] += 1;\\n        }\\n        boolean flag = true;\\n        for(int i=0; i<26; i++) {\\n            if (chars[i] < k && chars[i] > 0) flag = false;\\n        }\\n        if (flag) return s.length();\\n        int pos = 0, max = 0, start = 0;\\n        while(pos < s.length()) {\\n            if (chars[s.charAt(pos) - \'a\'] < k) {\\n                max = Math.max(max, longestSubstring(s.substring(start, pos), k));\\n                start = pos + 1;\\n            }\\n            pos++;\\n        }\\n        max = Math.max(max, longestSubstring(s.substring(start), k));\\n        return max;\\n    }\\n}","subPath":"","fileName":"solution.java"}}')}}]);