"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[640],{70640:n=>{n.exports=JSON.parse('{"java":{"language":"java","code":"class Solution\\n{\\n    public int minDifference(int arr[], int n) {\\n        if (n==0) return 0;\\n        if (n==1) return arr[0];\\n        if (n==2) return Math.abs(arr[1] - arr[0]);\\n        int sum = 0;\\n        for(int i=0; i<n; i++) {\\n            sum += arr[i];\\n        }\\n        int W = sum/2;\\n        int[][] dp = new int[n+1][W+1];\\n        for(int i=1; i<= n; i++) {\\n            for(int j=1; j<= W; j++) {\\n                if (arr[i-1] <= j) {\\n                    dp[i][j] = Math.max(dp[i-1][j], arr[i-1] + dp[i-1][j-arr[i-1]]);\\n                } else {\\n                    dp[i][j] = dp[i-1][j];\\n                }\\n            }\\n        }\\n        return Math.abs(sum - 2*dp[n][W]);\\n    }\\n}","subPath":"","fileName":"solution.java"}}')}}]);